  {"name": "twist", "label": "Helix Twist", "type": "float", "default": 1.0, "min": 0.5, "max": 3.0},
  {"name": "density", "label": "Glow Density", "type": "float", "default": 1.5, "min": 0.5, "max": 3.0},
  {"name": "hue", "label": "Color Hue", "type": "float", "default": 0.55, "min": 0.0, "max": 1.0},
  {"name": "glowInt", "label": "Glow Intensity", "type": "float", "default": 2.5, "min": 0.0, "max": 5.0}
]
*/

layout(location = 0) out vec4 pc_fragColor;
uniform float iTime;
uniform vec2 iResolution;
uniform vec4 iMouse;

uniform float speed;
uniform float twist;
uniform float density;
uniform float hue;
uniform float glowInt;

#define MAX_STEPS 60
#define MAX_DIST 20.0
#define SURF_DIST 0.005

vec3 hueShift(vec3 col, float shift){
    vec3 m = vec3(0.55735)*dot(vec3(0.55735),col);
    vec3 u = col-m;
    vec3 v = cross(vec3(0.55735),u);
    col = u*cos(shift*6.28) + v*sin(shift*6.28) + m;
    return col;
}

mat2 rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

// DNA SDF
float sdDNA(vec3 p) {
    // 1. Twist the space along Z
    vec3 q = p;
    q.xy *= rot(q.z * twist);

    // 2. Double Helix Strands (Offset from center)
    // We create Two cylinders at X = +/- 0.8
    float dStrands = min(length(q.xy - vec2(0.8, 0.0)), length(q.xy + vec2(0.8, 0.0))) - 0.2;

    // 3. Rungs (Connecting bars)
    // Repeat every 0.5 units
    float zRepeat = mod(q.z, 0.5) - 0.25;
    float dRungs = length(vec2(q.y, zRepeat)) - 0.1; // Cylinder Rung
    dRungs = max(dRungs, abs(q.x) - 0.8); // Clip between strands

    // Combine
    // Smooth min makes it look organic/liquid
    return min(dStrands, dRungs); 
}

float map(vec3 p) {
    // Global Waving Motion
    float wave = sin(p.z * 0.5 + iTime * speed * 0.5) * 0.5;
    p.x += wave;
    p.y += cos(p.z * 0.3 + iTime * speed * 0.3) * 0.3;

    // Map DNA
    float d = sdDNA(p);

    return d;
}

void main() {
    vec2 uv = (gl_FragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;

    // --- MOUSE CONTROL ---
    float yaw = 1.57; // Start side view
    float pitch = 0.0;

    if (iMouse.z > 0.0 || iMouse.x > 0.0) {
        yaw += (iMouse.x / iResolution.x - 0.5) * 6.0;
        pitch += (iMouse.y / iResolution.y - 0.5) * 3.0;
    } else {
        yaw += iTime * 0.2; // Auto rotate
    }

    // Scrolling Z position
    float tPos = iTime * speed;
    float camDist = 5.0;

    vec3 ro = vec3(0.0, 0.0, tPos);
    ro.x += camDist * cos(pitch) * sin(yaw);
    ro.y += camDist * sin(pitch);
    ro.z += camDist * cos(pitch) * cos(yaw);

    vec3 ta = vec3(0.0, 0.0, tPos);

    vec3 f = normalize(ta - ro);
    vec3 r = normalize(cross(vec3(0,1,0),f));
    vec3 u = cross(f,r);
    vec3 rd = normalize(f + r*uv.x + u*uv.y);

    float t = 0.0;
    float acc = 0.0;

    // Volumetric March
    for(int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * t;
        float d = map(p);

        // Accumulate Glow
        // Sharper curve for DNA strands
        float distFactor = 0.02 / (0.02 + abs(d));
        acc += distFactor * density * 0.1;

        t += max(abs(d) * 0.5, 0.05); 
        if(t > MAX_DIST) break;
    }

    // Colors
    vec3 aqua = vec3(0.0, 1.0, 0.8);
    vec3 purple = vec3(0.5, 0.0, 1.0);

    // Interior GLow = Aqua, Exterior = Purple
    vec3 col = mix(purple, aqua, clamp(acc, 0.0, 1.0));

    col = hueShift(col, hue - 0.55);
    col *= acc * glowInt;

    // Fog
    col *= 1.0 / (1.0 + t * t * 0.01);

    pc_fragColor = vec4(col, 1.0);
}
