uniform float iTime;
uniform vec2 iResolution;
uniform vec4 iMouse;
uniform sampler2D iChannel0;

// User Defined Uniforms
uniform float u_speed;
uniform float u_complexity;
uniform float u_glow_intensity;
uniform float u_fractal_zoom;

out vec4 fragColor;

// --- UTILS ---
mat2 rot(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, -s, s, c);
}

// 3D Noise for volumetric texture
float hash(vec3 p) {
    p = fract(p * 0.3183099 + 0.1);
    p *= 17.0;
    return fract(p.x * p.y * p.z * (p.x + p.y + p.z));
}

// Tech-Fractal SDF
// Uses a Folded Box (Menger-like) approach for "Tech" looks
float map(vec3 p) {
    float time = iTime * u_speed * 0.1;

    // Slow rotation of the fractal structure
    p.xy *= rot(time * 0.3);
    p.xz *= rot(time * 0.2);

    float scale = 1.0;
    float d = 1e10;

    // Iterative folding for fractal complexity
    for(int i = 0; i < 4; i++) {
        p = abs(p) - vec3(0.5, 0.8, 0.5) * u_fractal_zoom;

        if (p.x < p.y) p.xy = p.yx;
        if (p.x < p.z) p.xz = p.zx;
        if (p.y < p.z) p.yz = p.zy;

        p.z -= 0.1 * float(i);
        p.xy *= rot(0.5 + float(i) * 0.2);

        // Constructing the "Tech" beams/boxes
        float box = (length(p.xy) - 0.02 * u_complexity) / scale;
        d = min(d, box);

        scale *= 1.4;
        p *= 1.4;
        p -= vec3(0.1, 0.2, 0.1);
    }

    return d;
}

// Normal calculation for lighting
vec3 getNormal(vec3 p) {
    vec2 e = vec2(0.001, 0.0);
    return normalize(vec3(
        map(p + e.xyy) - map(p - e.xyy),
        map(p + e.yxy) - map(p - e.yxy),
        map(p + e.yyx) - map(p - e.yyx)
    ));
}

void main() {
    // Correct Aspect Ratio UVs
    vec2 uv = (gl_FragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

    // Setup Camera
    float camDist = 6.0;
    vec3 ro = vec3(0.0, 0.0, -camDist);
    vec3 rd = normalize(vec3(uv, 1.5)); // FOV roughly 1.5

    // Slight camera orbit
    float rx = iTime * 0.1 + (iMouse.x / iResolution.x) * 2.0;
    float ry = (iMouse.y / iResolution.y) * 2.0;
    ro.yz *= rot(ry);
    ro.xz *= rot(rx);
    rd.yz *= rot(ry);
    rd.xz *= rot(rx);

    // Raymarching
    float t = 0.0;
    float glow = 0.0;
    float hit = 0.0;

    // Volumetric/Transparency Raymarch Loop
    for(int i = 0; i < 80; i++) {
        vec3 p = ro + rd * t;
        float d = map(p);

        // Glow accumulation for transparency effect
        // High density near surfaces
        glow += exp(-d * 4.0) * u_glow_intensity;

        // Logic for "Tech" circuits (grid-like glow)
        float techGrid = sin(p.x * 20.0) * sin(p.y * 20.0) * sin(p.z * 20.0);
        if(techGrid > 0.8) glow += 0.05 * u_glow_intensity;

        if(d < 0.001) {
            hit = 1.0;
            // Instead of breaking, we slow down to see "inside" (transparency)
            d = 0.01; 
        }

        t += d * 0.6; // Relaxed step for transparency
        if(t > 15.0) break;
    }

    // Coloring
    vec3 baseColor = vec3(0.1, 0.4, 0.8); // Deep Cyber Blue
    vec3 accentColor = vec3(0.0, 1.0, 0.9); // Electric Cyan

    // Dynamic color shifting
    vec3 color = mix(baseColor, accentColor, sin(iTime * 0.5) * 0.5 + 0.5);

    // Apply Glow and Distance Fog
    vec3 finalColor = color * glow * 0.08;
    finalColor += accentColor * pow(glow * 0.05, 2.0); // Hot spots

    // Vignette
    float vig = 1.0 - length(uv * 0.6);
    finalColor *= vig;

    // Output
    fragColor = vec4(finalColor, 1.0);
}
