uniform float iTime;
uniform vec2 iResolution;
uniform vec4 iMouse;
uniform sampler2D iChannel0;

// Parameters defined in the JSON
uniform float u_speed;
uniform float u_camera_dist;
uniform float u_fov;
uniform float u_glow_intensity;

out vec4 fragColor;

// --- UTILS ---
mat2 rot(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, -s, s, c);
}

// Smooth Minimum for organic blending
float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

// --- SHAPES ---
// A twisted "Infinity Bowtie" based on a modified torus
float sdInfinityBowtie(vec3 p) {
    float t = iTime * u_speed * 0.5;

    // Twist the space
    p.xz *= rot(p.y * 0.5 + t);

    // Lemniscate-inspired transformation
    float angle = atan(p.x, p.z);
    float dist = length(p.xz);

    // Pinched radius to create the bowtie effect
    float r1 = 2.5; 
    float r2 = 0.4 + 0.3 * cos(angle * 2.0); // The "pinch"

    // Add vertical oscillation
    p.y += sin(angle * 2.0 + t) * 0.5;

    vec2 q = vec2(dist - r1, p.y);
    return length(q) - r2;
}

// Scene Map
float map(vec3 p) {
    // Subtle world distortion
    float d = sdInfinityBowtie(p);

    // Add some "inner" filaments for complexity
    float d2 = sdInfinityBowtie(p * 1.5) / 1.5;
    return smin(d, d2, 0.5);
}

// Color Palette
vec3 getPalette(float t) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263, 0.416, 0.557);
    return a + b * cos(6.28318 * (c * t + d + iTime * 0.1));
}

void main() {
    vec2 uv = (gl_FragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    float time = iTime * u_speed;

    // 1. CAMERA SYSTEM (Parallel Rail)
    float angle = time * 0.2;
    vec3 ro = vec3(sin(angle) * u_camera_dist, cos(angle * 0.5) * 2.0, cos(angle) * u_camera_dist);
    vec3 ta = vec3(0.0, 0.0, 0.0);

    vec3 f = normalize(ta - ro);
    vec3 r = normalize(cross(vec3(0, 1, 0), f));
    vec3 u = cross(f, r);
    vec3 rd = normalize(f * u_fov + uv.x * r + uv.y * u);

    // 2. RAYMARCHING
    float t = 0.0;
    float glow = 0.0;
    float d = 0.0;
    vec3 p;

    for(int i = 0; i < 80; i++) {
        p = ro + rd * t;
        d = map(p);

        // Accumulate glow based on proximity to surface
        glow += 0.05 / (0.1 + abs(d));

        if(abs(d) < 0.001 || t > 30.0) break;
        t += d * 0.8; // Relaxed step for smoother gradients
    }

    // 3. COLORING & FADING
    vec3 col = vec3(0.0);

    // Background Gradient
    col = mix(vec3(0.01, 0.02, 0.05), vec3(0.0), length(uv));

    if(t < 30.0) {
        // Calculate Normal for shading
        vec2 e = vec2(0.01, 0.0);
        vec3 n = normalize(vec3(
            map(p + e.xyy) - map(p - e.xyy),
            map(p + e.yxy) - map(p - e.yxy),
            map(p + e.yyx) - map(p - e.yyx)
        ));

        float diff = max(dot(n, normalize(vec3(1, 2, 3))), 0.0);
        float fresnel = pow(1.0 - max(dot(n, -rd), 0.0), 4.0);

        vec3 baseCol = getPalette(p.y * 0.2 + time * 0.1);
        col = baseCol * diff;
        col += fresnel * baseCol * 2.0;
    }

    // Apply smooth glow and volumetric fade
    vec3 glowCol = getPalette(time * 0.05) * glow * u_glow_intensity;
    col += glowCol * 0.1;

    // Atmosphere / Distance Fade
    col = mix(col, vec3(0.0, 0.01, 0.02), 1.0 - exp(-0.05 * t));

    // Contrast and Gamma Correction
    col = pow(col, vec3(0.8));
    col *= 1.2;

    fragColor = vec4(col, 1.0);
}
