uniform float iTime;
uniform vec2 iResolution;
uniform vec4 iMouse;
uniform sampler2D iChannel0;

uniform float u_speed;
uniform float u_height;
uniform float u_wire_width;
uniform float u_glow_intensity;

out vec4 fragColor;

// --- Utilities ---
mat2 rot(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, -s, s, c);
}

// 3D Noise for terrain and space matter
float hash(vec2 p) {
    return fract(sin(dot(p, vec2(127.1, 311.7))) * 43758.5453123);
}

float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    vec2 u = f * f * (3.0 - 2.0 * f);
    return mix(mix(hash(i + vec2(0.0, 0.0)), hash(i + vec2(1.0, 0.0)), u.x),
               mix(hash(i + vec2(0.0, 1.0)), hash(i + vec2(1.0, 1.0)), u.x), u.y);
}

float fbm(vec2 p) {
    float v = 0.0;
    float a = 0.5;
    for (int i = 0; i < 5; i++) {
        v += a * noise(p);
        p *= 2.0;
        a *= 0.5;
    }
    return v;
}

// --- Terrain and Objects ---
float getTerrain(vec3 p) {
    float h = fbm(p.xz * 0.4) * 2.5;
    h += fbm(p.xz * 2.0) * 0.2; // Small rocks
    return p.y + h;
}

// Distance to wireframe grid
float sdGrid(vec3 p) {
    vec2 g = abs(fract(p.xz * 2.0 + 0.5) - 0.5) / 2.0;
    float grid = min(g.x, g.y);
    return grid;
}

float map(vec3 p) {
    float terrain = getTerrain(p);
    return terrain;
}

// Simple star/matter field
vec3 getSpaceMatter(vec3 dir) {
    float s = pow(hash(dir.xy * 512.0), 50.0);
    vec3 stars = vec3(s) * 2.0;
    stars *= vec3(1.0, 0.4, 0.2); // Reddish space dust
    return stars;
}

void main() {
    vec2 uv = (gl_FragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    float t = iTime * u_speed;

    // Camera Setup: Flying over the Martian surface
    vec3 ro = vec3(0.0, u_height, t);
    // Add some subtle bobbing/path swaying
    ro.x += sin(t * 0.2) * 2.0;

    vec3 ta = ro + vec3(sin(t * 0.1) * 0.5, -0.4, 2.0);

    vec3 fwd = normalize(ta - ro);
    vec3 right = normalize(cross(vec3(0, 1, 0), fwd));
    vec3 up = cross(fwd, right);
    vec3 rd = normalize(fwd + uv.x * right + uv.y * up);

    // Raymarching
    float d = 0.0;
    float dist = 0.0;
    vec3 p;
    for(int i = 0; i < 80; i++) {
        p = ro + rd * dist;
        d = map(p);
        if(abs(d) < 0.001 || dist > 40.0) break;
        dist += d * 0.6;
    }

    vec3 col = vec3(0.01, 0.0, 0.0); // Deep red space background

    if(dist < 40.0) {
        // We hit the terrain
        float grid = sdGrid(p);
        float wire = smoothstep(u_wire_width, 0.0, grid);

        // Base terrain color (dark Martian red)
        vec3 terrainCol = vec3(0.15, 0.03, 0.01);

        // Wireframe glow (bright orange/red)
        vec3 wireCol = vec3(1.0, 0.3, 0.1) * 2.5;

        // Mix terrain and wireframe
        col = mix(terrainCol, wireCol, wire);

        // Distance fog / Atmosphere
        float fog = exp(-0.15 * dist);
        vec3 atmosCol = vec3(0.8, 0.2, 0.05) * u_glow_intensity;
        col = mix(atmosCol * 0.2, col, fog);

        // Add a scanline or data pulse effect
        float pulse = sin(p.z * 1.5 - t * 4.0) * 0.5 + 0.5;
        col += wireCol * pulse * 0.2 * wire;
    } else {
        // Space matter
        col = getSpaceMatter(rd);
    }

    // Horizon Glow
    float horizon = pow(1.0 - max(dot(rd, vec3(0, 1, 0)), 0.0), 8.0);
    col += vec3(0.8, 0.2, 0.0) * horizon * u_glow_intensity * 0.5;

    // Post processing
    col = pow(col, vec3(0.8)); // Gamma correction/Contrast
    col *= 1.0 - dot(uv, uv) * 0.5; // Vignette

    fragColor = vec4(col, 1.0);
}
