uniform float iTime;
uniform vec2 iResolution;
uniform vec4 iMouse;
uniform sampler2D iChannel0;

// User Parameters
uniform float u_speed;
uniform float u_complexity;
uniform float u_transparency;
uniform float u_glow_intensity;

out vec4 fragColor;

// --- UTILS ---
mat2 rot(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, -s, s, c);
}

float sdGyroid(vec3 p, float scale, float thickness, float bias) {
    p *= scale;
    return abs(dot(sin(p), cos(p.zxy)) - bias) / scale - thickness;
}

// Fractal displacement function
float fractal(vec3 p) {
    float d = 0.0;
    float amp = 1.0;
    float freq = 1.0;
    for(int i = 0; i < 4; i++) {
        p.xy *= rot(0.5);
        p.yz *= rot(0.3);
        d += abs(dot(sin(p * freq), cos(p.zxy * freq))) * amp;
        amp *= 0.5;
        freq *= 2.0;
    }
    return d;
}

// --- MAP / SDF ---
float map(vec3 p) {
    // Rotating the whole scene slowly
    p.xz *= rot(iTime * 0.1 * u_speed);
    p.yz *= rot(iTime * 0.07 * u_speed);

    // Core Sphere
    float sphere = length(p) - 2.5;

    // Gyroid layers
    float g1 = sdGyroid(p, 3.0 + sin(iTime * 0.2) * 0.5, 0.03, 0.5);
    float g2 = sdGyroid(p + 10.0, 6.0, 0.02, 0.3);

    // Fractal Tech details
    float f = fractal(p * 0.8) * 0.2;

    // Combine layers: The globe is formed by intersecting the gyroids with a sphere shell
    float globe = max(sphere, - (length(p) - 2.4)); // Thin shell
    float tech = max(sphere, mix(g1, g2, 0.5));

    return min(tech, sphere + f * u_complexity);
}

// Normal calculation
vec3 getNormal(vec3 p) {
    vec2 e = vec2(0.01, 0);
    return normalize(vec3(
        map(p + e.xyy) - map(p - e.xyy),
        map(p + e.yxy) - map(p - e.yxy),
        map(p + e.yyx) - map(p - e.yyx)
    ));
}

void main() {
    // Correct Aspect Ratio
    vec2 uv = (gl_FragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

    // Camera
    vec3 ro = vec3(0.0, 0.0, -7.0);
    vec3 rd = normalize(vec3(uv, 1.5));

    // Raymarching
    float t = 0.0;
    float glow = 0.0;
    vec3 col = vec3(0.0);

    for(int i = 0; i < 80; i++) {
        vec3 p = ro + rd * t;
        float d = map(p);

        // Accumulate glow based on proximity to the surface
        glow += 0.01 / (0.05 + abs(d));

        if(d < 0.001 || t > 15.0) break;
        t += d * 0.5; // Slower steps for transparency feel
    }

    if(t < 15.0) {
        vec3 p = ro + rd * t;
        vec3 n = getNormal(p);
        vec3 viewDir = normalize(ro - p);

        // Fresnel effect for transparency look
        float fresnel = pow(1.0 - max(dot(n, viewDir), 0.0), 3.0);

        // Shading
        vec3 techColor = vec3(0.1, 0.5, 0.8); // Cyber blue
        techColor = mix(techColor, vec3(0.7, 0.2, 0.9), sin(p.y + iTime) * 0.5 + 0.5); // Color shift

        col = techColor * fresnel * 2.0;

        // Add specular highlights
        float spec = pow(max(dot(reflect(-viewDir, n), vec3(0.577)), 0.0), 32.0);
        col += spec * 0.5;

        // Edge lighting
        col += vec3(0.5, 0.8, 1.0) * (1.0 - u_transparency) * 0.2;
    }

    // Post-processing: Add global glow and tech pulse
    col += vec3(0.1, 0.4, 0.6) * glow * u_glow_intensity * 0.1;

    // Scanline / Grid effect
    float grid = sin(uv.y * 200.0 + iTime * 10.0) * 0.02;
    col += grid * (1.0 - length(uv));

    // Vignette
    col *= 1.2 - length(uv) * 0.7;

    fragColor = vec4(col, 1.0);
}
