uniform float iTime;
uniform vec2 iResolution;
uniform vec4 iMouse;
uniform sampler2D iChannel0;

// User Parameters
uniform float u_speed;
uniform float u_camera_dist;
uniform float u_complexity;
uniform float u_glow_intensity;

out vec4 fragColor;

// --- UTILS ---
mat2 rot(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, -s, s, c);
}

// 3D Noise for tech-displacement
float hash(vec3 p) {
    p = fract(p * 0.3183099 + 0.1);
    p *= 17.0;
    return fract(p.x * p.y * p.z * (p.x + p.y + p.z));
}

// Tech Fractal SDF
// We use a Folded Menger-inspired fractal with glass-like transparency logic
float map(vec3 p) {
    float scale = 1.0;
    float d = 1e10;

    // Slight twist over Z
    p.xy *= rot(p.z * 0.1);

    vec3 p_orig = p;

    for(int i = 0; i < 4; i++) {
        p = abs(p) - vec3(1.2, 1.8, 1.2); // Symmetrical folding
        if (p.x < p.y) p.xy = p.yx;
        if (p.x < p.z) p.xz = p.zx;
        if (p.y < p.z) p.yz = p.zy;

        // Fractal scaling
        float s = 1.8 / clamp(dot(p,p), 0.1, 1.0);
        p *= s;
        p -= vec3(0.5, 2.0, 0.5); // Tech offset
        scale *= s;
    }

    // Create "plates" and "wires"
    float box = (length(p.xz) - 0.1) / scale;
    float plates = (abs(p.y) - 0.05) / scale;

    return min(box, plates);
}

// Universe Path for the fly-by
vec2 getPath(float z) {
    return vec2(sin(z * 0.2) * 2.0, cos(z * 0.11) * 1.5);
}

void main() {
    vec2 uv = (gl_FragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

    // 1. Time & Path
    float t = iTime * u_speed * 0.2;
    vec3 pathPos = vec3(0.0, 0.0, t);
    pathPos.xy += getPath(pathPos.z);

    // 2. Parallel Rail Camera Strategy
    // The camera follows a parallel rail so the fractal structures aren't just hitting the lens
    float railOffset = u_camera_dist; 
    vec2 railVec = vec2(cos(t * 0.15), sin(t * 0.12)) * railOffset;

    vec3 ro = pathPos;
    ro.xy += railVec; 

    vec3 ta = vec3(0.0, 0.0, t + 4.0);
    ta.xy += getPath(ta.z) + railVec * 0.7; // Look slightly towards the center path

    // Camera Transform
    vec3 cw = normalize(ta - ro);
    vec3 cp = vec3(0.0, 1.0, 0.0);
    vec3 cu = normalize(cross(cw, cp));
    vec3 cv = normalize(cross(cu, cw));
    vec3 rd = normalize(uv.x * cu + uv.y * cv + 1.5 * cw);

    // 3. Raymarching
    float d = 0.0, t_dist = 0.0;
    vec3 col = vec3(0.0);
    float accGlow = 0.0;

    // Volumetric / Transparent accumulation loop
    for(int i = 0; i < 80; i++) {
        vec3 p = ro + rd * t_dist;
        p.xy -= getPath(p.z); // Center universe relative to path

        d = map(p);

        // Accumulate "tech glow" based on proximity
        accGlow += smoothstep(0.1, 0.0, d) * u_glow_intensity;

        // Transparency logic: if we are close, we add color but keep marching
        if(d < 0.01) {
            float noise = hash(floor(p * 20.0)); // Tech detail noise
            vec3 layerCol = 0.5 + 0.5 * cos(vec3(0.0, 1.0, 2.0) + p.z * 0.5);
            col += layerCol * 0.05 * (1.0 + noise);
            d = 0.05; // Skip ahead to simulate transparency
        }

        t_dist += max(d * 0.6, 0.02);
        if(t_dist > 40.0) break;
    }

    // 4. Shading & Post
    // Background deep tech blue
    vec3 bg = vec3(0.01, 0.02, 0.05) * (1.0 - length(uv));
    col = mix(bg, col, 1.0 - exp(-0.05 * t_dist));

    // Adding the "Fractal Glow"
    vec3 glowColor = vec3(0.1, 0.5, 1.0); // Cyan/Blue tech
    col += accGlow * 0.02 * glowColor;

    // Scanlines / Tech Overlay
    float scanline = sin(gl_FragCoord.y * 0.5) * 0.02;
    col -= scanline;

    // Vignette
    col *= 1.0 - dot(uv, uv) * 0.4;

    // Tone mapping
    col = smoothstep(0.0, 1.1, col * 1.5);

    fragColor = vec4(col, 1.0);
}
